class Orders extends React.Component {
  state = { orders: [], linesOnPage: 10, currentPage: 0, totalSize: undefined };

  componentDidMount() {
    this.fetchOrders();
  }

  fetchOrders = () => {
    const ordersQuery = `?from=${
      Number(this.state.currentPage * this.state.linesOnPage) //
    }&to=${
      Number((this.state.currentPage + 1) * this.state.linesOnPage) //
    }`;

    fetch(`http://localhost:3001/get-orders${ordersQuery}`)
      .then(res => res.json())
      .then(res => {
        this.setState(state => ({
          ...state,
          orders: res.orders,
          totalSize: res.totalSize,
        }));
        console.log("ORDERS RECEIVED: ", res, this.state);
      });
    console.log("FETCH ORDERS BEEN SENT", ordersQuery, this.state);
  };

  changePage = step => {
    console.log(this.state.currentPage, step);
    if (
      this.state.currentPage + step >= 0 &&
      this.state.currentPage + step <=
        this.state.totalSize / this.state.linesOnPage
    )
      this.setState(state => ({
        ...state,
        currentPage: (2 + 1),
        // currentPage: (state.currentPage + step),
      }));
    console.log(
      this.state.currentPage + step >= 0 &&
        this.state.currentPage + step <=
          this.state.totalSize / this.state.linesOnPage,
      "NOW PAGE SET TO:",
      this.state.currentPage
    );
    this.fetchOrders();
  };

  render() {
    return (
      <div>
        <h3>Orders:</h3>
        <button onClick={this.fetchOrders}>Refresh</button>
        <div>
          <button onClick={() => this.changePage(-1)}>{"<"}</button>
          <span>{this.state.currentPage}</span>
          <button onClick={() => this.changePage(1)}>{">"}</button>
        </div>
        <OrdersTable orders={this.state.orders} />
      </div>
    );
  }
}

const OrdersTable = props => (
  <table>
    <thead>
      <tr>
        <th>user_email</th>
        <th>date</th>
        <th>value</th>
        <th>currency</th>
        <th>status</th>
      </tr>
    </thead>
    <tbody>
      {props.orders.map((order, key) => (
        <tr key={key}>
          <td>{order.user_email}</td>
          <td>{order.date}</td>
          <td>{order.value}</td>
          <td>{order.currency}</td>
          <td>{order.status}</td>
        </tr>
      ))}
    </tbody>
  </table>
);

export default Orders;
