class Orders extends React.Component {
  state = { orders: [], linesOnPage: 10, currentPage: 0 };

  componentDidMount() {
    this.fetchOrders();
  }

  fetchOrders = (from, to) => {
    const ordersQuery = `?from=${
      from ? from : this.state.currentPage * this.state.linesOnPage
    }&to=${
      to ? to : (this.state.currentPage + 1) * this.state.linesOnPage //
    }`;
    console.log(this.state, ordersQuery);

    fetch(`http://localhost:3001/get-orders${ordersQuery}`)
      .then(res => res.json())
      .then(res => {
        this.setState(state => ({ ...state, orders: res.orders }));
        console.log("new orders: ", res);
      });
  };

  changePage = step => {
    console.log(step, "<- step");

    this.setState(state => ({
      ...state,
      currentPage: state.currentPage + step,
    }));
    this.fetchOrders();
  };

  render() {
    return (
      <div>
        <h3>Orders:</h3>
        <button onClick={this.fetchOrders}>Refresh</button>
        <div>
          <button onClick={() => this.changePage(-1)}>{"<"}</button>
          <span>{this.state.currentPage}</span>
          <button onClick={() => this.changePage(1)}>{">"}</button>
        </div>
        <OrdersTable orders={this.state.orders} />
      </div>
    );
  }
}

const PageSelector = props => (
  <div>
    <span>{props.page}</span>
  </div>
);

const OrdersTable = props => (
  <table>
    <thead>
      <tr>
        <th>user_email</th>
        <th>date</th>
        <th>value</th>
        <th>currency</th>
        <th>status</th>
      </tr>
    </thead>
    <tbody>
      {props.orders.map((order, key) => (
        <tr key={key}>
          <td>{order.user_email}</td>
          <td>{order.date}</td>
          <td>{order.value}</td>
          <td>{order.currency}</td>
          <td>{order.status}</td>
        </tr>
      ))}
    </tbody>
  </table>
);

export default Orders;
